import React, {useState} from 'react';
import {Link, Outlet} from "react-router-dom";
import logo from '../assets/logo.svg'

const Layout: React.FC = () => {
    const [currentPage, setCurrentPage] = useState<string>(window.location.pathname)
    const menuClick = () => {
        setTimeout(() => setCurrentPage(window.location.pathname), 0);
        console.log(currentPage)
    }
    return (
        <div className={"bg-white min-h-screen flex"}>
            <header className={`w-screen h-96 bg-sky-400 flex flex-row justify-between px-36 text-xl text-white py-8`}>
                <img src={logo} className={"h-7"} alt={"Drewl.com"}/>
                <span className={"flex gap-7 flex-row align-center"}>
                    <Link to={"/"}><p onClick={menuClick}
                                      className={`${currentPage === '/' ? "font-extrabold" : null}`}>Converter</p></Link>
                    <Link to={"/currencies"}><p onClick={menuClick}
                                                className={`${currentPage === '/currencies' ? "font-extrabold" : null}`}>Exchange rate</p></Link>
                </span>
            </header>
            {/*Fixme: paste to center horizontally*/}
            <main className={"absolute top-60  min-h-[270px] rounded-xl overflow-hidden p-6 w-1/2 bg-white self-center shadow-2xl"}>
                <div className={"flex items-center"}>
                    <Outlet/>
                </div>
            </main>
            <footer className={"absolute bottom-0 text-center w-screen"}>Copyright Â© 2022 Drewl Task 2022. All rights
                reserved
            </footer>
        </div>
    );
};

export default Layout;
