import {actionTypes} from "../action-types";
import axios from "axios";
import {Actions} from "../actions";
import {Dispatch} from "react";
import {log} from "util";
import {keyboard} from "@testing-library/user-event/dist/keyboard";

export const ConvertMoney = (amount: number, from: string, to: string) => {
    return {
        type: actionTypes.CONVERT,
        payload: {
            request_amount: amount,
            from: from,
            to: to,
            response_amount: 0,
        }
    }
}

export const fetchData = (amount?: number, from?: string, to?: string) => {
    const API_KEY = 'FWcJsFtVZIgaioJaaTsmxsiGH2i53NOvy5boCoNc'
    return async (dispatch: Dispatch<Actions>) => {
        try {
            dispatch({type: actionTypes.FETCH_WEATHER_REQUEST})
            let response: any;
            if (amount && from && to) {
                // @ts-ignore
                response = await axios.get("https://api.currencyapi.com/v3/convert")
                console.log(response.data)
            }else {
                response = await axios.get(`https://api.currencyapi.com/v3/latest?apikey=${API_KEY}`)
                console.log("response:", response.data)
                console.log("response:", response.data.data.map((key:number) => key))
                let to = response.data.map((key: number) => response.data[key][0]);
                let amount = response.data.map((key: number) => response.data[key][1]);
                dispatch({type: actionTypes.FETCH_WEATHER_SUCCESS, payload: {
                        from: 'usd',
                        to: to,
                        response_amount: amount
                    }})
            }
        } catch (error) {
            dispatch({type: actionTypes.FETCH_WEATHER_FAILURE, payload: "ooops, something went wrong!"})
        }
    }
}
