import DataItem from "../components/DataItem";
import {useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import {ConvertMoney, fetchData} from "../state/action-creators";
import {State} from "../state";

const Currencies = () => {
    const dispatch = useDispatch()
    const {response_amount, from, to} = useSelector((state: State) => {
        return state.converter
    })
    useEffect(()=>{
        // @ts-ignore
        dispatch(fetchData())
    }, [])

    return (
            <div className="w-2/4 bg-white mx-auto mt-[-150px] shadow-2xl flex justify-center items-center overflow-hidden border-b border-gray-200 sm:rounded-lg">
                <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                    <tr>
                        <th
                            scope="col"
                            className="px-6 py-4 text-left text-xs text-center font-extrabold font-medium text-gray-500 uppercase tracking-wider"
                        >
                            From
                        </th>
                        <th
                            scope="col"
                            className="px-6 text-center py-3 text-left font-extrabold text-xs font-medium text-gray-500 uppercase tracking-wider"
                        >
                            To
                        </th><th
                            scope="col"
                            className="px-6 py-3 text-left font-extrabold text-center text-xs font-medium text-gray-500 uppercase tracking-wider"
                        >
                            Amount
                        </th>
                    </tr>
                    </thead>
                    {response_amount.map((item, key) => {
                    <tbody className="bg-white divide-y divide-gray-200">
                        <DataItem/>
                        <DataItem/>
                        <DataItem/>
                        <DataItem/>
                        <DataItem/>
                        <DataItem/>
                        <DataItem/>
                        <DataItem/>

                    </tbody>
                    })}
                </table>
            </div>
    )
}

export default Currencies
