import {actionTypes} from "../action-types";
import axios from "axios";
import { ThunkDispatch } from 'redux-thunk'
import {DispatchProp} from "react-redux";
import {Actions} from "../actions";
import {Dispatch} from "react";
import {InitialStateType} from "../reducers/convertReducer";
import converter from "../../pages/Converter";
import reducers from "../reducers";

const API_KEY = 'E2moV4qG3M1ZMoVIlPp1JXHmyWHqtoYm'

export const ConvertMoney = (amount: number, from: string, to: string) => {
    return {
        type: actionTypes.CONVERT,
        payload: {
            request_amount: amount,
            from: from,
            to: to,
            response_amount: 0,
        }
    }
}

export const fetchWeatherRequest = () => ({
    type: actionTypes.FETCH_WEATHER_REQUEST
})
export const fetchWeatherSuccess = (info: string) => ({
    type: actionTypes.FETCH_WEATHER_SUCCESS,
    payload: info
})

export const fetchWeatherFailure = (error: string) => ({
    type: actionTypes.FETCH_WEATHER_FAILURE,
    payload: error
})
export const fetchData = () : ThunkDispatch<void, > => {
    return (dispatch) => {
        dispatch(fetchWeatherRequest)
        axios.get(`https://data.fixer.io/api/latest?access_key=${API_KEY}`)
            .then(res => {
                const info = res.data
                console.log(info)
                dispatch(fetchWeatherSuccess(info))
            }).catch(error => {
            dispatch(fetchWeatherFailure(error.message))
        })
    }
}
