import {useDispatch, useSelector} from "react-redux";
import {bindActionCreators} from 'redux'
import {State, actionCreators} from "../state";
import {useEffect} from "react";

const Converter = () => {
    const dispatch = useDispatch()
    const {money, from, to} = useSelector((state: State) => state.converter)
    const {ConvertMoney} = bindActionCreators(actionCreators, dispatch)
    useEffect(() => {
        console.log(money, from, to)
    }, [money, from, to])
    return (
        <div
            className={"absolute top-60 left-0 right-0 my-0 mx-auto min-h-[270px] rounded-xl overflow-hidden p-6 w-1/2 bg-white shadow-2xl"}>
            <div className={"flex flex-col gap-5 items-center justify-center"}>
                <div className={"font-bold flex flex-row w-full overflow-hidden gap-1"}>
                    <input placeholder={"E.g: 15 usd in rub"} className={"w-[80%] bg-slate-100 rounded-xl p-4"}/>
                    <button onClick={() => ConvertMoney(1200, "usd", "rub")}
                            className={"bg-sky-600 px-7 py-3 w-[20%] self-end text-white font-bold justify-self-end rounded-xl"}>Convert
                    </button>
                </div>
                <p className={"text-pink-600 font-bold text-center text-2xl"}>Result: </p>
            </div>
        </div>
    )
}

export default Converter
